import { useEffect, useState } from 'react';

function App() {
  const [events, setEvents] = useState([]);

  // Fetch events from client-service
  const fetchEvents = () => {
    fetch('http://localhost:6001/api/events')
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error('Error fetching events:', err));
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  // Purchase a ticket
  const buyTicket = (id) => {
    fetch(`http://localhost:6001/api/events/${id}/purchase`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message); // confirmation
        fetchEvents(); // refresh the list
      })
      .catch((err) => console.error('Error purchasing ticket:', err));
  };

  return (
    <div>
      <h1>TigerTix Events</h1>
      <ul>
        {events.length === 0 && <li>No events available</li>}
        {events.map((event) => (
          <li key={event.id}>
            <strong>{event.name}</strong> — {event.date} — Tickets: {event.ticketsAvailable}{' '}
            <button 
              onClick={() => buyTicket(event.id)}
              aria-label={`Buy ticket for ${event.name}`}
            >
              Buy Ticket
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
