import { useEffect, useState } from 'react';

function App() {
  const [events, setEvents] = useState([]);

  // Fetch events from client-service API
  useEffect(() => {
    fetch('http://localhost:6001/api/events')
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error('Error fetching events:', err));
  }, []);

  return (
    <div>
      <h1>TigerTix Events</h1>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            <strong>{event.name}</strong> — {event.date} — Tickets: {event.ticketsAvailable}{' '}
            <button onClick={() => buyTicket(event.id)}>Buy Ticket</button>
          </li>
        ))}
      </ul>
    </div>
  );

  // Function to purchase a ticket
  function buyTicket(id) {
    fetch(`http://localhost:6001/api/events/${id}/purchase`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message); // show confirmation
        // Update UI by refetching events
        return fetch('http://localhost:6001/api/events')
          .then((res) => res.json())
          .then((data) => setEvents(data));
      })
      .catch((err) => console.error('Error purchasing ticket:', err));
  }
}

export default App;
