import { useEffect, useState } from 'react';
import './App.css'; // Make sure this imports your CSS

function App() {
  const [events, setEvents] = useState([]);
  const [purchaseMessage, setPurchaseMessage] = useState('');

  // Fetch events from client-service
  const fetchEvents = () => {
    fetch('http://localhost:6001/api/events')
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error('Error fetching events:', err));
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  // Purchase a ticket
  const buyTicket = (id, name) => {
    fetch(`http://localhost:6001/api/events/${id}/purchase`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    })
      .then((res) => res.json())
      .then((data) => {
        setPurchaseMessage(`Successfully purchased ticket for ${name}`);
        fetchEvents(); // refresh the list
      })
      .catch((err) => {
        console.error('Error purchasing ticket:', err);
        setPurchaseMessage('Error purchasing ticket.');
      });
  };

  return (
    <main>
      <h1>TigerTix Events</h1>

      {/* Dynamic message for screen readers */}
      <div aria-live="polite">
        {purchaseMessage && <p>{purchaseMessage}</p>}
      </div>

      <ul>
        {events.length === 0 && <li>No events available</li>}
        {events.map((event) => (
          <li key={event.id}>
            <div>
              <strong>{event.name}</strong> — {event.date} — Tickets: {event.ticketsAvailable}
            </div>
            <button
              onClick={() => buyTicket(event.id, event.name)}
              aria-label={`Buy ticket for ${event.name}`}
            >
              Buy Ticket
            </button>
          </li>
        ))}
      </ul>
    </main>
  );
}

export default App;
